/// <reference types="react" />
import * as React from 'react';
import * as PropTypes from 'prop-types';
import { ButtonInterface } from './ButtonInterface';
/**
 * Represents the props of the Button component for React.
 */
export interface ButtonProps extends ButtonInterface, React.ButtonHTMLAttributes<HTMLButtonElement> {
    /**
     * @hidden
     */
    children?: React.ReactNode;
}
/**
 * @hidden
 */
export interface ButtonState {
    active: boolean;
}
/**
 * @hidden
 */
export default class Button extends React.Component<ButtonProps, ButtonState> {
    static propTypes: {
        children: PropTypes.Requireable<any>;
        look: PropTypes.Requireable<any>;
        primary: PropTypes.Requireable<any>;
        selected: PropTypes.Requireable<any>;
        togglable: PropTypes.Requireable<any>;
        icon: PropTypes.Requireable<any>;
        iconClass: PropTypes.Requireable<any>;
        imageUrl: PropTypes.Requireable<any>;
        imageAlt: PropTypes.Requireable<any>;
    };
    static defaultProps: {
        look: string;
        primary: boolean;
        selected: boolean;
        togglable: boolean;
    };
    constructor(props: ButtonProps);
    componentWillReceiveProps(props: ButtonProps): void;
    render(): JSX.Element;
    private activate();
    private handleClick(event);
}
